import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";
import * as actions from "../../store/actions";
import './Login.scss';
import { divide } from 'lodash';
import { Fragment } from 'react';


class Login extends Component {
    constructor(props) {
        super(props);
        this.state={
            username:"",
            password:""
        }
    }
    handleonchangeUsername=(event)=>{
        this.setState({
            username: event.target.value 

        })
        console.log(event.target.value)
    }
    handleonchangePassword=(event)=>{
        this.setState({
            password: event.target.value 

        })
    }

    handleLogin=()=>{
        this.state.username
    }

    render() {


        return (
            <Fragment>
               <div class="form-login">
                   <div class="frontpager">Sign Up</div>
                   <div class="form-field">
                    <input type="text" class="form-input"
                     value={this.state.username}
                     onChange={event => this.handleonchangeUsername(event)}></input>
                    <label for="name" form="form-label" >Name</label>
                   </div>
                   <div class="form-field">
                   <input type="password" class="form-input"
                   value={this.state.username}
                   onChange={event => this.handleonchangePassword(event)}></input>
                    <label for="name" form="form-label">Password</label> 
                    <i class="far fa-eye"></i>
                   </div>
                   <div class="form-btn">
                       <button onClick={()=>{this.handleLogin()}}>Login</button>
                   </div>
                   <div class="form-register">
                       <span>Forgot your password?</span>
                   </div>

               </div>


            </Fragment>
        )

    }
}

const mapStateToProps = state => {
    return {
        lang: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) => dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
